import React,{useStat} from 'react'
import axios from 'axios'
import FormProduct from '../views/FormProduct';
import ListAllProducts from './ListAllProducts'


const CreateNewProduct = () => {
  const [products, setProductValue] = useState([])

  const handleSubmit  = ({newProduct}) => {
    axios.post("http://localhost:5000/api/products/",newProduct)
    .then(res => addProductToList = (newProduct) => setProductValue((prev) => [...prev, newProduct]) )
    .catch(err => console.log(err))
}
        
         
  const addProductToList = (newProduct) => setProductValue((prev) => [...prev, newProduct]) 
  
  return (
    <div>
        <FormProduct onSubmit={addProductToList} productValue={{}}/>
        <ListAllProducts allProducts={products} />
    </div>
  )
}

export default CreateNewProduct
