import React, { useState,useEffect } from 'react'
import './ProductForms.css'
const ProductForms = (initProductValues, h) => {

    const [title,setTitle]=useState("")
    const [price,setPrice]=useState("")
    const [description,setDescription]=useState("")

    useEffect(() => {
        setTitle(initProductValues.title)
        setPrice(initProductValues.price)
        setDescription(initProductValues.description)
    }, [initProductValues])

    const handleSubmit = (e) => {
        e.preventDefault()
        onSubmit({title,price,description})
        setTitle("")
        setPrice("")
        setDescription("")
    }
  return (
    <div className='container'>
        <h1>Product Manager</h1>
        <form >
            <label>Title</label>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)}/>
            <label>Price</label>
            <input type="text" value={price} onChange={(e) => setPrice(e.target.value)}/>
            <label>Description</label>
            <input type="text" value={description} onChange={(e) => setDescription(e.target.value)}/>
            <button className='btn' onClick={handleSubmit} type="submit">Create</button>
        </form>
    </div>
  )
}

export default ProductForms
