import React,{useState,usee} from 'react'
import axios from 'axios'
import FormProduct from '../views/FormProduct';
import ListAllProducts from './ListAllProducts'


const CreateNewProduct = ({addProductToList}) => {
  const [products, setProducts] = useState([])

  useEffect(() => {
      axios.get("http://localhost:5000/api/products/")
          .then((res) => {
              setProducts(res.data.products)
          })
          .catch(e => console.log(e))
  }, [])

  const addToList = (newProduct) => setProducts((prev) => [...prev, newProduct]) 
  const handleSubmit = ({productValue}) => {
        axios.post("http://localhost:5000/api/products/",productValue)
        .then(res => addProductToList(res.data.newProduct))
        .catch(err => console.log(err))
    }
  return (
    <div>
        <FormProduct onSubmit={handleSubmit} productValue={{}}/>
        <ListAllProducts  />
    </div>
  )
}

export default CreateNewProduct
